        return n + calc(n + 1);
    }
}

public int func(int k) {
    if (k <= 0) {
        return 0;
    }
    if (k % 2 == 0) {
        return k + func(k - 1);
    } else {
        return func(k - 1);
    }
}

public void stackTrace(int x) {
    if (x > 0) {
        stackTrace(x - 1);
        System.out.print(x + " ");
    }
}

public String doSomething(String str) {
    int len = str.length();
    if (len <= 1) {
        return str;
    }
    return str.substring(len - 1) + doSomething(str.substring(0, len - 1));
}

public int fib(int n) {
    if (n <= 1) return n;
    return fib(n - 1) + fib(n - 2);
}

public int whatIsIt(int[] nums, int index) {
    if (index == nums.length) {
        return 0;
    }
    return nums[index] + whatIsIt(nums, index + 1);
}

